package datatypes;

public class PrimitiveDatatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//primitive datatypes=keyword=memory is fix
		//Number+Non Decimal=byte,short,int,long
		
		byte rollno;
		
		rollno=5;
		
		System.out.println("my roll no is "+rollno);

		//for Short datatypes
		
		short d;
		
		d=555;
		
		System.out.println("value of d is "+d);
		
		//for int datatypes
		
		int a;
		
		a=45555555;
		
		System.out.println("value of a is "+a);
		
		//for long datatypes
		
		long mynumber;
		
		mynumber=123123123123123l;
		
		System.out.println("long number is "+mynumber);
		
	}

}
