package datatypes;

public class PrimitiveDatatypes2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//For Decimal number Float and Double
		
		float marks;
		
		marks=88.99f;
		
		System.out.println("my marks is "+marks);
		
		//float Round off Example
		
		float point;
		
		point=55.99999999999999999999f;
		
		System.out.println("Round off point is "+point);
		
		//Double Datatypes Example
		
		double marks2;
		
		marks2=55.12548569d;
		
		System.out.println("marks2 is "+marks2);

	}

}
